mapper.select:
select j.uid,j.submit_id,j.submit_time,j.uid,j.username,j.uid,j.pid,j.status,j.share,
j.time,j.memory,j.score,j.oi_rank_score,j.length,j.language,j.cid,j.cpid,j.judger,p.problem_id as display_pid,p.title
from judge j,problem p

    p.id = j.pid AND j.cid = 0 AND j.cpid = 0 AND p.auth = 1 AND
    (p.is_public = true OR j.uid = #{myUid} OR #{isRoot} OR #{myUid} IN (
        select gm.uid
        from group_member gm
        where (gm.auth = 3 or gm.auth = 4 or gm.auth = 5) and p.gid = gm.gid
        )
    )


            AND p.problem_id like concat('%',#{searchPid},'%')


            AND p.problem_id = #{searchPid}



        AND j.username like concat('%',#{username},'%')


        AND j.status = #{status}


        AND j.uid = #{uid}


order by j.submit_time DESC,j.submit_id DESC

select j.uid,j.submit_id,j.submit_time,j.uid,j.username,j.uid,cp.display_id,cp.display_title as title,
j.status,j.share,j.time,j.memory,j.score,j.length,j.language,j.cid,j.cpid,j.judger
from judge j,contest_problem cp

    j.pid=cp.pid AND j.cid = cp.cid

       AND j.cid = #{cid}



            AND cp.display_id like concat('%',#{displayId},'%')


            AND cp.display_id = #{displayId}



        AND j.username like concat('%',#{username},'%')


        AND j.status = #{status}


        AND j.uid = #{uid}


        AND #{startTime} > j.submit_time


        AND j.submit_time >= #{startTime}



            AND (#{sealRankTime} > j.submit_time OR j.uid=#{sealTimeUid})


            AND  #{sealRankTime} > j.submit_time



order by j.submit_time DESC,j.submit_id DESC

SELECT COUNT(IF(status=-3,status,NULL)) AS pe, COUNT(IF(status=-2,status,NULL)) AS ce, COUNT(IF(status=-1,status,NULL)) AS wa, COUNT(IF(status=0,status,NULL)) AS ac, COUNT(IF(status=1,status,NULL)) AS tle, COUNT(IF(status=2,status,NULL)) AS mle, COUNT(IF(status=3,status,NULL)) AS re, COUNT(IF(status=4,status,NULL)) AS se, COUNT(IF(status=8,status,NULL)) AS pa, COUNT(*) AS total FROM judge
pid=#{pid} and cpid = #{cpid} and cid = #{cid}
and submit_time >= #{startTime}
and #{sealRankTime} > submit_time
and uid NOT IN
#{item}
