mapper.select:
select j1.*,p.problem_id AS display_pid,p.title from ( select j.submit_id,j.submit_time,j.username,j.uid,j.pid,j.status,j.share, j.time,j.memory,j.score,j.oi_rank_score,j.length,j.language,j.cid,j.cpid,j.eid,j.epid,j.judger,j.is_public,j.gid from judge j
j.cid = 0 AND j.cpid = 0 AND j.eid = 0 AND j.epid = 0
AND j.username like concat('%',#{username},'%')
AND j.status = #{status}
AND j.uid = #{uid}
) j1 left join problem p on j1.pid = p.id
p.auth = 1 AND
j1.gid = #{gid}
j1.is_public
AND p.problem_id like concat('%',#{searchPid},'%')
AND p.problem_id = #{searchPid}
order by submit_time DESC,j.submit_id DESC

select j.submit_id,j.submit_time,j.uid,j.username,j.uid,cp.display_id,cp.display_title as title,
j.status,j.share,j.time,j.memory,j.score,j.length,j.language,j.cid,j.cpid,j.eid,j.epid,j.judger
from judge j right join
(
select display_title,display_id,cid,pid from contest_problem



            AND display_id like concat('%',#{displayId},'%')


            AND display_id = #{displayId}



) cp
on j.pid = cp.pid AND j.cid = cp.cid


        AND j.cid = #{cid}


        AND j.username like concat('%',#{username},'%')


        AND j.status = #{status}


        AND j.uid = #{uid}


        AND j.submit_time >= #{startTime}



            AND (#{sealRankTime} > j.submit_time OR j.uid=#{sealTimeUid})


            AND  #{sealRankTime} > j.submit_time



order by j.submit_time DESC,j.submit_id DESC

select j.submit_id,j.submit_time,j.uid,j.username,j.uid,ep.display_id,ep.display_title as title,
j.status,j.share,j.time,j.memory,j.score,j.length,j.language,j.cid,j.cpid,j.eid,j.epid,j.judger
from judge j right join
(
select display_title,display_id,eid,pid from exam_problem



            AND display_id like concat('%',#{displayId},'%')


            AND display_id = #{displayId}



) ep
on j.pid = ep.pid AND j.eid = ep.eid


        AND j.eid = #{eid}


       AND false
       AND j.status = #{status}


        AND j.uid = #{uid}


        AND j.submit_time >= #{startTime}


order by j.submit_time DESC,j.submit_id DESC

SELECT COUNT(IF(status=-3,status,NULL)) AS pe, COUNT(IF(status=-2,status,NULL)) AS ce, COUNT(IF(status=-1,status,NULL)) AS wa, COUNT(IF(status=0,status,NULL)) AS ac, COUNT(IF(status=1,status,NULL)) AS tle, COUNT(IF(status=2,status,NULL)) AS mle, COUNT(IF(status=3,status,NULL)) AS re, COUNT(IF(status=4,status,NULL)) AS se, COUNT(IF(status=8,status,NULL)) AS pa, COUNT(*) AS total FROM judge
pid=#{pid} and cpid = #{cpid} and cid = #{cid}
and submit_time >= #{startTime}
and #{sealRankTime} > submit_time
and uid NOT IN
#{item}

SELECT COUNT(IF(status=-3,status,NULL)) AS pe, COUNT(IF(status=-2,status,NULL)) AS ce, COUNT(IF(status=-1,status,NULL)) AS wa, COUNT(IF(status=0,status,NULL)) AS ac, COUNT(IF(status=1,status,NULL)) AS tle, COUNT(IF(status=2,status,NULL)) AS mle, COUNT(IF(status=3,status,NULL)) AS re, COUNT(IF(status=4,status,NULL)) AS se, COUNT(IF(status=8,status,NULL)) AS pa, COUNT(*) AS total FROM judge
pid=#{pid} and epid = #{epid} and eid = #{eid}
and submit_time >= #{startTime}
and uid NOT IN
#{item}
and false
